var link=document.querySelector(".write-us"),popup=document.querySelector(".modal-block"),close=popup.querySelector(".modal-block-close"),form=popup.querySelector("form"),user_name=popup.querySelector("[name=user-name]"),user_mail=popup.querySelector("[name=user-mail]");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("modal-block-show"),user_name.focus()}),close.addEventListener("click",function(e){e.preventDefault(),popup.classList.remove("modal-block-show")}),form.addEventListener("submit",function(e){user_name.value&&user_mail.value||e.preventDefault()}),window.addEventListener("keydown",function(e){27===e.keyCode&&popup.classList.contains("modal-block-show")&&popup.classList.remove("modal-block-show")});
